package com.neo.sk.arachne2.cache

import akka.actor.Actor
import com.neo.sk.arachne2.common.{Cache2DB, Save2Cache}
import com.neo.sk.arachne2.models.newsDAO
import org.slf4j.LoggerFactory
import scala.concurrent.duration._
import scala.concurrent.ExecutionContext.Implicits.global
/**
 * Created by 王春泽 on 2016/4/11.
 */

class CacheActor(jobName:String,jobId:Long) extends Actor{
  private val logger=LoggerFactory.getLogger(getClass())
  private val cache2DBSchedule = context.system.scheduler.schedule(1.second,1 minutes,self,
    Cache2DB(jobName,jobId))

  private var newsList=List[String]()

  @throws[Exception](classOf[Exception])
  override def preStart()={
    logger.info("CacheActor "+self.path.name+"is starting.........")
  }
  @throws[Exception](classOf[Exception])
  override def postStop()={
    logger.info("CacheActor "+self.path.name+"is stoping..........")
  }

  override def receive:Receive={
    case Save2Cache(jobName:String,jobId:Long,dataType:String,content:String)=>
      newsList=content::newsList
      val n=newsList.length
      logger.info(s"----------save to cache the length= $n")

    case Cache2DB(jobName:String,jobId:Long)=>
      val newsTemp=newsList
      val sum=newsTemp.length
//      newsList=newsList.dropRight(sum)
      val sum2=newsList.length
      logger.info(s"----------the news sum is $sum and the left is $sum2")

      println(newsTemp)
      println(newsTemp.length)
      val target=newsTemp.filter(_.split("\u0001").length==12).map{line =>
        val arr = line.split("\u0001")
        (arr(0),arr(1),arr(2).split(" ").head,arr(3),arr(4),arr(5).toLong,arr(6),
          arr(7),arr(8).toInt,arr(9),arr(10),arr(11))
      }
      if(target.nonEmpty) {
        newsDAO.insertData(target)onComplete {
          case scala.util.Success(_) =>
            newsList=newsList.dropRight(sum)
            println("--insert to database successful!!--")
          case scala.util.Failure(e) =>
            println(s"database operation failed >>>>>>>>>>>>>>>>>$e")
        }
      }

    case unknow@_ => logger.info("unknow message: "+unknow + "in"+context.self.path.name+"from "+context.sender().path.name)
  }


}
